import React, { SyntheticEvent, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
import ResultComp from "./ResultComp";
import styled from "styled-components";
import patternlight from "../components/assets/images/pattern-background-desktop-light.svg";

interface IProps {
  title: string;
  icon: string;
  questions: Array<Tquestions>;
}

type Tquestions = {
  question: string;
  options: string[];
  answer: string;
};

const Container = styled.div`
  height: 100vh;
  border: blue solid 1px;
  display: flex;
  background-color: var(--lightblue);
  background-image: url(${patternlight});
  background-position: center;
`;

const Questiontile = styled.div`
  flex: 1;
  margin: 20px;
  width: 564px;
  height: 86px;
  //border: var(--red) solid 1px;
  border-radius: 5px;

  background-color: var(--verylightblue);
  pointer: cursor !important;
`;
const QuestionComp = () => {
  const { state } = useLocation();
  const { title, questions } = state;
  const [questionCount, setQuestionCount] = useState(
    Object.keys(questions).length
  );
  // counter will track the id of the question which must be on the screen.
  const [counter, setCounter] = useState(0);
  const [reversecounter, setReverseCounter] = useState(
    Object.keys(questions).length
  );
  const [selectedOption, setSelectedOption] = useState<string | null>();
  const [actualAnswer, setActualAnswers] = useState<string | null>();
  const [correctAnswer, setCorrectAnswer] = useState<boolean>();
  //console.log("selectedOption", selectedOption);

  const answerCheck = () => {
    console.log(selectedOption);
    selectedOption === actualAnswer
      ? setCorrectAnswer(true)
      : setCorrectAnswer(false);
  };

  const storeAnswer = (e: SyntheticEvent) => {
    debugger;
    setSelectedOption(e.currentTarget.textContent);

    if (selectedOption) {
      console.log(selectedOption);
    }
  };

  if (counter !== reversecounter) {
    return (
      <>
        <Container>
          {questions.map((i: any, idx: number) => {
            if (idx === counter) {
              return (
                <div key={idx}>
                  <div>{i.question}</div>
                  <ul>
                    {i.options.map((o: string, idx: number) => {
                      return (
                        <Questiontile
                          key={idx}
                          onClick={(e: SyntheticEvent) => {
                            setActualAnswers(i.answer);
                            storeAnswer(e);
                          }}>
                          {o}
                        </Questiontile>
                      );
                    })}
                  </ul>
                  <button
                    onClick={() => {
                      answerCheck();
                    }}>
                    Submit your answer
                  </button>
                </div>
              );
            }
          })}
        </Container>
      </>
    );
  } else {
    return <ResultComp />;
  }
};

export default QuestionComp;
